mapper.select:
SELECT goodId, goodName, goodPrice, goodImage, goodState, buyerNum, categoryId
FROM Good
WHERE goodId = #{goodId}

mapper.update:
UPDATE Good
SET buyerNum = buyerNum + 1
WHERE goodid = #{goodId}

UPDATE Good
SET goodName = #{goodName}, goodPrice = #{goodPrice}, goodDesc = #{goodDesc},
    buyerNum = #{buyerNum}, goodImage = #{goodImage}, categoryId = #{categoryId}
WHERE goodId = #{goodId}
