mapper.select:
SELECT * FROM Orders
WHERE

    goodId = #{goodId}


    userId = #{userId}


    LIMIT #{pageSize} OFFSET #{pageSize} * (#{pageNum} - 1)

SELECT COUNT(*) FROM Orders
WHERE

    userId = #{userId}


    goodId = #{goodId}

mapper.update:
UPDATE Orders
SET orderState = #{orderState}
WHERE orderId = #{orderId}
